import React from 'react';
import { Form, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import styled from 'styled-components';
import logo from "../imgs/logo2.png";

const SigninPage = (props) => {
    const [showPassword, setShowPassword] = useState(false);

    const handleLogin = (event) => {
        event.preventDefault();
        // Implement your login logic here
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    return (
        <StyledContainer>
            <FormBox>
                <LogoImg src={logo} alt="Logo" />
                <Form onSubmit={handleLogin}>
                    <StyledFormGroup controlId="formBasicId">
                        <StyledFormControl type="text" placeholder="아이디 입력" />
                    </StyledFormGroup>

                    <StyledFormGroup controlId="formBasicPassword">
                        <InputGroup>
                            <StyledFormControl
                                type={showPassword ? "text" : "password"}
                                placeholder="비밀번호 입력"
                            />
                            <InputGroup.Append>
                                <Button variant="outline-secondary" onClick={togglePasswordVisibility}>
                                    {showPassword ? "숨기기" : "보기"}
                                </Button>
                            </InputGroup.Append>
                        </InputGroup>
                    </StyledFormGroup>

                    <StyledButton variant="primary" type="submit">
                        로그인
                    </StyledButton>
                </Form>
            </FormBox>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #f7f7f7;
`;

const FormBox = styled.div`
  padding: 40px;
  background: #ffffff;
  border-radius: 8px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  width: 100%;
  max-width: 400px; /* Adjusted the max-width for a wider form */
`;

const StyledFormGroup = styled(Form.Group)`
  margin-bottom: 20px; 
`;

const StyledFormControl = styled(Form.Control)`
  font-size: 16px; 
  padding: 10px; 
  
  ::placeholder {
    color: #A9A9A9; /* Grey placeholder text */
  }
`;

const StyledButton = styled(Button)`
  width: 100%;
  margin-top: 20px;
`;

const LogoImg = styled.img`
  height: 20vh;
  width: auto;
  margin-bottom: 20px;
`;

export default SigninPage;
