import React from 'react';
import styled from 'styled-components';
const AnnouncementPage = (props) => {
    return (
        <Container>
            <MiddleContainer>
                <AnnouncementTitle>공지사항</AnnouncementTitle>
                <NewsTitle>
                    <SmallNewsTitle>
                        <LeftNewsTitle>한소연 소식</LeftNewsTitle>
                        <RightNewsTitle>
                            <ExitButton>더 보기 ></ExitButton>
                        </RightNewsTitle>
                    </SmallNewsTitle>
                </NewsTitle>
                <MainTitle>
                    <TopMainTitle>

                    </TopMainTitle>
                </MainTitle>
            </MiddleContainer>
        </Container>
    );
};

const Container = styled.div`
  display: flex;
  flex: 1;
  background-color: red;
  height: 100vh;
  align-items: center;
  justify-content: center;
`

const MiddleContainer = styled.div`
  display: flex;
  height: 500px;
  width: 1000px;
  background-color: blue;
  flex-direction: column;
`
const AnnouncementTitle = styled.div`
  display: flex;
  flex: 1;
  align-items:center;
  background-color: green;
  font-size: 40px;
  font-weight: 700;
`

const NewsTitle = styled.div`
  display: flex;
  flex: 2;
  background-color: blue;
  align-items: center
`
const SmallNewsTitle = styled.div`
  display: flex;
  flex-direction: row;
  background-color: orange;
  height: 60px;
  flex: 1;
`

const LeftNewsTitle = styled.div`
  display: flex;
  flex: 5;
  background-color: purple;
  height: 60px;
  font-size: 20px;
  align-items: center;
  margin-left: 0.5rem;
`

const RightNewsTitle = styled.div`
  display: flex;
  flex: 5;
  background-color: darkgray;
  height: 60px;
  font-weight: 700;
  align-items: center;
  justify-content: right;
`

const ExitButton = styled.div`
  display: flex;
  width: 100px;
  height: 40px;
  text-align: center;
  background-color: aqua;
  color: white;
  font-size: 20px;
  float: right;
  align-items: center;
  justify-content: center;
`
const MainTitle = styled.div`
  display: flex;
  flex: 7;
  flex-direction: column;
  background-color: yellow;
`
const TopMainTitle = styled.div`
  display: flex;
  flex-direction: row;
  border-top: 2px solid hotpink;
  height: 70px;
  flex: 1;
  align-items: center;
`



export default AnnouncementPage;
