import React, {useEffect} from 'react';
import styled from 'styled-components';
import defaultProfilePic from '../imgs/default_profile.png';
import {useNavigate} from "react-router-dom";
import {useCookies} from "react-cookie";
import {useUserStore} from "../stores";
import axios from "axios";

const MyInfoPage = (props) => {
    const navigate = useNavigate();
    const [cookies, setCookie, removeCookie] = useCookies(['token']);
    const { user, setUser } = useUserStore();

    useEffect(() => {
        if (cookies.token) {
            axios.get('http://localhost:8050/api/auth/currentUser', {
                headers: {
                    Authorization: `Bearer ${cookies.token}`
                }
            }).then(response => {
                const fetchedUser = response.data;
                setUser(fetchedUser);
            }).catch(error => {
                // 토큰이 유효하지 않은 경우
                console.error("Token verification failed:", error);
                handleLogout();
            });
        }
    }, []);

    const handleLogout = () => {
        removeCookie('token');
        setUser(null);
        navigate("/");
    };

    const handleEditProfile = () => {
        // 프로필 수정 로직, 예를 들어 navigate('/edit-profile')를 사용할 수 있습니다.
    };

    return (
        <StyledContainer>
            <InfoBox>
                <ProfileSection>
                    <ProfileImage src={defaultProfilePic} alt="프로필 사진" />
                    <Name>{user.userName + "님" || 'No Name'}</Name>
                    <Email>{user.userEmail || 'No Email'}</Email>
                    <EditProfileButton onClick={handleEditProfile}>프로필 수정</EditProfileButton>
                </ProfileSection>
                <InfoSection>
                    <DetailRow>
                        <Label>휴대폰 번호:</Label>
                        <Value>010-1234-5678</Value>
                    </DetailRow>
                </InfoSection>
            </InfoBox>
        </StyledContainer>
    );
};

const StyledContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const InfoBox = styled.div`
  width: 90%;
  max-width: 500px;
  background: white;
  padding: 20px;
  border-radius: 10px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;

const ProfileSection = styled.div`
  text-align: center;
  margin-bottom: 20px;
`;

const ProfileImage = styled.img`
  width: 100px;
  height: 100px;
  border-radius: 50%;
  margin-bottom: 10px;
`;

const Name = styled.h2`
  margin: 0;
  color: #381E1F;
`;

const Email = styled.p`
  color: gray;
`;

const InfoSection = styled.div`
  // 스타일링 추가
`;

const DetailRow = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 10px 0;
`;

const Label = styled.span`
  font-weight: bold;
`;

const Value = styled.span`
  color: gray;
`;

const EditProfileButton = styled.button`
  padding: 8px 12px;
  border-radius: 20px;
  background-color: #F7E600;
  color: #381E1F; 
  border: none;
  cursor: pointer;
  font-size: 15px;
  margin-top: 5px;
  &:hover {
    background-color: #e6d700;
  }
`;

export default MyInfoPage;
