import React from "react";
import {Container} from "react-bootstrap";
import aboutPolicy from "../imgs/aboutLogo.jpg";
import styled from "styled-components";

const AboutPolicyPage = () => {
    return (
            <Contai1ner>
                <CCContainer>
                    <BigAboutContainer>
                        <AboutContainer>
                            <TopAboutContainer>
                                <ImageContainer>
                                    <img src={aboutPolicy} alt="About Image" />
                                    <OverlayTextContainer>
                                        <OverlayTextTop>
                                            <h2>우리의 생각</h2>
                                        </OverlayTextTop>
                                        <OverlayTextBottom>
                                            <h2>Policy</h2>
                                        </OverlayTextBottom>
                                    </OverlayTextContainer>
                                </ImageContainer>
                            </TopAboutContainer>
                            <BottomAboutContainer>
                                <BottomCenterContainer>
                                    <br/>
                                    <h2>매번,</h2>
                                    <h2>마음과 조건이 듬뿍 담긴 서비스입니다.<br/><br/></h2>
                                    <h3>한국에는 아직 놀라울 정도로 매력 넘치는 지역들로 가득합니다.</h3>
                                    <h3>더 많은 사람들이 한국 각지에 부담없이 나가서 지역에 들어가,</h3>
                                    <h3>한국의 사랑스럽고 풍부한 매력을 아는 세상을 만들고 싶어합니다.<br/><br/></h3>
                                    <h3>"한소연"은 그런 생각에서 태어난 서비스입니다.<br/><br/></h3>
                                    <h3>모르는 지역에 가서 재밌고, 처음 만나는 사람들을 봐서 재밌습니다.</h3>
                                    <h3>매번 가서 준 분들이 도움을 통해 지역에 빠져들고,</h3>
                                    <h3>뭔가 멋진 발견이나 경험을 많이 하고, 눈치채면 자신에게 있어서의 특별한 지역이 되어 돌아오는,</h3>
                                    <h3>그런 새로운 여행의 형태를 제안할 수 있으면하는 본망입니다.</h3>
                                    <TextContainer>
                                        <h2>대표. 한소연 일동</h2>
                                    </TextContainer>
                                </BottomCenterContainer>
                            </BottomAboutContainer>
                        </AboutContainer>
                    </BigAboutContainer>
                </CCContainer>
        </Contai1ner>
    )
}

const Contai1ner = styled.div`
  position: absolute;
  width: 100%;
  justify-content: center;
  height: 100vh;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr; /* Single column for the entire container */
  grid-template-rows: auto 1fr auto auto; /* Four rows for BigAboutContainer, FeatureContainer, FlowContainer, and SocialContainer */
  grid-template-areas:
    "BigAboutContainer"
    "FeatureContainer"
    "FlowContainer"
    "SocialContainer";
`;

const BigAboutContainer = styled.div`
  display: flex;
  grid-template-columns: 1fr;
  grid-template-rows: auto 1fr; /* Adjust rows as needed */
  height: 70vh; /* Change height to auto for responsiveness */
  justify-content: center;
  align-items: center;
  margin-top: 4rem;
  width: 90%;
  border-radius: 10% 10% 0 0 / 10% 10% 0 0; /* 부드러운 흐물한 효과를 위한 border-radius 설정 */
`;

const AboutContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%; /* Change width to 100% for responsiveness */
  max-width: 1200px; /* Add max-width for responsiveness */
`;
const CCContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`
const TopAboutContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  flex: 5.5;
`;

const ImageContainer = styled.div`
  display: flex;
  position: relative;
  flex: 1;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 50%;
  img {
    width: 100%;
    height: 300px;
    max-width: 100%; /* Ensure the image does not exceed its natural size */
    border-radius: 10% 10% 0 0 / 10% 10% 0 0; /* 부드러운 흐물한 효과를 위한 border-radius 설정 */
  }
`;
const OverlayTextContainer = styled.div`
  position: absolute;
  justify-content: center;
  width: 100%;
  height: 200px;
  display: flex;
  flex-direction: column;
  padding: 20px;
  top: 50%; /* 부모 요소의 50% 높이에서 정렬 시작 */
  transform: translateY(-50%); /* 부모 요소의 50% 높이만큼 위로 이동 */
`;

const OverlayTextTop = styled.div`
  border-radius: 5px;
  text-align: center;

  h2 {
    font-size: 32px;
    font-weight: 700;
    color: white;
  }
`;
const TextContainer = styled.div`
    display: flex;
    background-color: red;
    width: 100%;
    justify-content: flex-end;
  h2 {
    font-size: 28px;
  }
`
const OverlayTextBottom = styled.div`
  border-radius: 5px;
  text-align: center;

  h2 {
    font-size: 28px;
    font-weight: 600;
    color: white;
  }
`;

const BottomAboutContainer = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
  background-color: #61dafb;
`;

const BottomCenterContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: gray;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-top: 1rem;
  border-radius: 5px;
  border: 2px solid #FBCEB1;

  h2 {
    font-size: 36px;
    color: orange;
    font-weight: 700;
  }
  h3 {
    font-size: 20px;
  }
`;

export default AboutPolicyPage;
