import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { XMLParser } from 'fast-xml-parser';

const RecommendPage = () => {
    const [touristSpots, setTouristSpots] = useState([]);
    const [keyword, setKeyword] = useState(""); // 검색어 상태
    const [pageNo, setPageNo] = useState(1); // 페이지 번호

    const serviceKey = 'YRUALCBQQWvU6w/tG7ZkUtWtjAeaO9bJjyummGjvfF9SjR0QYO+CRveierZlwe97v5toXybLb6aoFCl1sZ8q4Q==';

    useEffect(() => {
        fetchData(keyword, pageNo);
    }, [keyword, pageNo]);

    const fetchData = async (searchKeyword, pageNo) => {
        try {
            const encodedServiceKey = encodeURIComponent(serviceKey);
            const encodedKeyword = encodeURIComponent(searchKeyword || "강원"); // 검색어가 없는 경우 기본값
            const url = `http://apis.data.go.kr/B551011/KorService1/searchKeyword1?serviceKey=${encodedServiceKey}&MobileApp=AppTest&MobileOS=ETC&pageNo=${pageNo}&numOfRows=10&listYN=Y&keyword=${encodedKeyword}`;
            const response = await axios.get(url);

            const parser = new XMLParser();
            const jsonObj = parser.parse(response.data);

            const spots = jsonObj.response.body.items.item;
            setTouristSpots(spots);
        } catch (error) {
            console.error('Error fetching data: ', error);
        }
    };

    return (
        <div>
            <input
                type="text"
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
                placeholder="지역 검색"
            />
            <button onClick={() => fetchData(keyword, 1)}>검색</button>
            {Array.isArray(touristSpots) && touristSpots.length > 0 ? (
                <ul>
                    {touristSpots.map((spot, index) => (
                        <li key={index}>
                            <h3>{spot.title}</h3>
                            {spot.firstimage && <img src={spot.firstimage} alt={spot.title} />}
                            <br/>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading...</p>
            )}
            <button onClick={() => setPageNo(prev => prev - 1)}>이전</button>
            <button onClick={() => setPageNo(prev => prev + 1)}>다음</button>
        </div>
    );
};

export default RecommendPage;
