import React from "react";
import styled from "styled-components";
import {useNavigate} from "react-router-dom";
import About from "../imgs/About.png";

const AboutPage = () => {
    const navigate = useNavigate();

    const Detailbutton = () => {
        navigate("/");
    }
    return (
        <BigAboutContainer>
            <AboutContainer>
                <TopAboutContainer>
                    <ImageContainer>
                        <img src={About} alt="About Image" />
                        <OverlayTextContainer>
                            <OverlayTextTop>
                                <h2>한소연이란 무엇입니까?</h2>
                            </OverlayTextTop>
                            <OverlayTextBottom>
                                <h2>About</h2>
                            </OverlayTextBottom>
                        </OverlayTextContainer>
                    </ImageContainer>
                </TopAboutContainer>
                <BottomAboutContainer>
                    <BottomCenterContainer>
                        <h2>우리의 생각</h2>
                        <h3>한국에는 아직 놀라울 정도로 넘치는 지역으로 가득합니다.</h3>
                        <h3>더 많은 사람들이 한국 각지에 부담없이 나가서 지역 각지에서 즐기는 세상을 만들고 싶습니다!</h3>
                        <h3>"한소연"은 그런 생각에서 태어난 서비스 입니다.</h3>
                        <DeatailButton onClick={Detailbutton}>자세히 보기</DeatailButton>
                    </BottomCenterContainer>
                </BottomAboutContainer>
            </AboutContainer>
        </BigAboutContainer>
    )
}

const BigAboutContainer = styled.div`
  background-color: gray;
  display: flex;
  flex: 1;
  height: 700px;
  justify-content: center;
  align-items: center;
  margin-top: 8rem;
`
const AboutContainer = styled.div`
  display: flex;
  flex: 0.9;
  flex-direction: column;
  height: 600px;
  background-color: yellow;
`

const TopAboutContainer = styled.div`
  display: flex;
  flex: 5.5;
  background-color: blue;
`

const ImageContainer = styled.div`
  display: flex;
  position: relative;
  flex: 1;
  background-color: red;
  align-items: center;
img {
  width: 100%;
  height: 350px;
}

`
const OverlayTextContainer = styled.div`
  position: absolute;
  background-color: red;
  justify-content: center;
  width: 100%;
  height: 200px;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const OverlayTextTop = styled.div`
  background: green;
  padding: 10px;
  border-radius: 5px;
  text-align: center;

  h2 {
    font-size: 32px;
    font-weight: 700;
    color: white;
  }
`;

const OverlayTextBottom = styled.div`
  background: rgba(255, 255, 255, 0.7);
  padding: 10px;
  border-radius: 5px;
  text-align: center;
`;

const BottomAboutContainer = styled.div`
  display: flex;
  flex: 4.5;
  background-color: red;
  flex-direction: column;
  justify-content: center;
`
const BottomCenterContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: #FDF9EA;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  h2 {
    font-size: 36px;
    color: orange;
    font-weight: 700;
  }
  h3 {
    font-size: 28px;
    font-weight: 600;
  }
`
const DeatailButton = styled.button`
  background-color: #EF6C00;
  color: white;
  font-weight: 500;
  border: none;
  border-radius: 3px;
  width: 250px;
  height: 35px;
`
export default AboutPage;
