import React, { useState, useEffect } from 'react';
import {Navbar, Nav, Container, Button, Badge, Dropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import styled from "styled-components";
import {useNavigate} from 'react-router-dom';
import logo from '../imgs/logo.png';
import { useCookies } from 'react-cookie';
import { useUserStore } from '../stores';
import defaultProfilePic from '../imgs/default_profile.png';
import axios from "axios";
import AboutPage from "../Pages/AboutPage";
import RecruitPage from "../Pages/RecruitPage";

const Navigate = () => {
    const navigate = useNavigate();
    const [cookies, setCookie, removeCookie] = useCookies(['token']);
    const {user, setUser} = useUserStore();

    const isLoggedIn = cookies.token && user;
    const userType = cookies.userType;

    const [size, setSize] = useState("10");

    useEffect(() => {
        if (cookies.token) {
            console.log(userType)
            if(userType === "company"){
                axios.get('http://localhost:8050/api/auth/currentCompany', {
                    headers: {
                        Authorization: `Bearer ${cookies.token}`
                    }
                }).then(response => {
                    console.log(cookies.token)
                    // 토큰이 유효한 경우
                    const fetchedUser = response.data;
                    console.log(fetchedUser)
                    setUser(fetchedUser);
                }).catch(error => {
                    // 토큰이 유효하지 않은 경우
                    console.error("Token verification failed:", error);
                    handleLogout();
                });
            }else{
                axios.get('http://localhost:8050/api/auth/currentUser', {
                    headers: {
                        Authorization: `Bearer ${cookies.token}`
                    }
                }).then(response => {
                    console.log(cookies.token)
                    // 토큰이 유효한 경우
                    const fetchedUser = response.data;
                    console.log(fetchedUser)
                    setUser(fetchedUser);
                }).catch(error => {
                    // 토큰이 유효하지 않은 경우
                    console.error("Token verification failed:", error);
                    handleLogout();
                });
            }
        }
    }, []);


    const handleLogout = () => {
        removeCookie('token');
        setUser(null);
        navigate("/");
    };

    const MainButton = () => {
        navigate("/");
    }

    const handleLogin = () => {
        navigate("/login")
    };

    const handleSignUp = () => {
        navigate("/register")
    };

    const handleMyInfo = () => {
        navigate("/MyInfo")
    };

    const NewPageButton = () => {
        navigate("/newcourse");
    }
    const ReCommendPageButton = () => {
        navigate("/recommendcourse");
    }
    const ThemaPageButton = () => {
        navigate("/themecourse");
    }
    const RegionPageButton = () => {
        navigate("/regioncourse");
    }
    const ReviewPageButton = () => {
        navigate("/review");
    }
    const AnnouncementListPageButton = () => {
        navigate("/announcementlist");
    }
        const getProfilePicSrc = () => {
            if(userType === "company"){
                if (user.providerProfile === "hansoyeon/src/imgs/default_profile.png" || !user.providerProfile) {
                    return defaultProfilePic;
                }
                return user.providerProfile;
            }else{
                if (user.userProfile === "hansoyeon/src/imgs/default_profile.png" || !user.userProfile) {
                    return defaultProfilePic;
                }
                return user.userProfile;
            }
        };

        return (

            <Navbar fixed="top" style={{
                display: "flex",
                flex: "1",
            }}>
                <Navbar.Brand>
                    <Nav_Str>
                        <UserContainer>
                            <UserImg onClick={MainButton}>
                                <img className="logoImage" alt="Logo" src={logo}/>
                            </UserImg>
                        </UserContainer>
                        <PageNav style={{marginRight: `${size}rem`}}>
                            <AboutPageInfo onClick={AboutPageButton}>한소연이란?</AboutPageInfo>
                            <CoursePageInfo onClick={CoursePageButton}>코스</CoursePageInfo>
                            <RecruitPageInfo onClick={RecruitPageButton}>모집 일정</RecruitPageInfo>
                            <ReviewButton onClick={ReviewPageButton}>체험 후기</ReviewButton>
                            <AnnouncementPageInfo onClick={AnnouncementListPageButton}>공지 사항</AnnouncementPageInfo>
                        </PageNav>
                        <div>
                            {isLoggedIn ? (
                                <>
                                    <ProfileSection>
                                        {userType === 'company' ?
                                            <Badge bg="primary" style={{marginRight: '20px', fontSize: "16px"}}>기업
                                                회원</Badge>
                                            :
                                            <Badge bg="success" style={{marginRight: '20px', fontSize: "16px"}}>VVIP
                                            </Badge>
                                        }
                                        <span style={{
                                            marginRight: '20px',
                                            fontSize: '19px'
                                        }}>{user.userName + "님" || 'No Name'}</span>
                                        <StyledDropdown>
                                            <Dropdown.Toggle as={CustomToggle}>
                                                <ProfileImage src={getProfilePicSrc()} alt="Profile"/>
                                            </Dropdown.Toggle>
                                            <Dropdown.Menu>
                                                <Dropdown.Item onClick={handleMyInfo}>내 정보</Dropdown.Item>
                                                <Dropdown.Item href="#action/3.2">스케줄러</Dropdown.Item>
                                                <Dropdown.Item onClick={handleLogout}>로그아웃</Dropdown.Item>
                                            </Dropdown.Menu>
                                        </StyledDropdown>
                                    </ProfileSection>
                                </>
                            ) : (
                                <MypageConatiner>
                                    {isLoggedIn ? (
                                        <>
                                            <ProfileSection>
                                                {userType === 'company' ?
                                                    <Badge bg="primary" style={{marginRight: '20px', fontSize: "16px"}}>기업
                                                        회원</Badge>
                                                    :
                                                    <Badge bg="success" style={{marginRight: '20px', fontSize: "16px"}}>일반
                                                        회원</Badge>
                                                }
                                                {userType === 'company' ?
                                                    <span style={{
                                                        marginRight: '20px',
                                                        fontSize: '19px'
                                                    }}>{user.providerName + "님" || 'No Name'}</span>
                                                    :
                                                    <span style={{
                                                        marginRight: '20px',
                                                        fontSize: '19px'
                                                    }}>{user.userName + "님" || 'No Name'}</span>
                                                }
                                                <StyledDropdown>
                                                    <Dropdown.Toggle as={CustomToggle}>
                                                        <ProfileImage src={getProfilePicSrc()} alt="Profile"/>
                                                    </Dropdown.Toggle>

                                                    <Dropdown.Menu>
                                                        <Dropdown.Item onClick={handleMyInfo}>내 정보</Dropdown.Item>
                                                        <Dropdown.Item href="#action/3.2">스케줄러</Dropdown.Item>
                                                        <Dropdown.Item onClick={handleLogout}>로그아웃</Dropdown.Item>
                                                    </Dropdown.Menu>
                                                </StyledDropdown>
                                            </ProfileSection>
                                        </>
                                    ) : (
                                        <>
                                            <Button color="inherit" variant="light" onClick={handleLogin}
                                                    style={{marginRight: '0.5rem'}}>로그인</Button>
                                            <Button color="inherit" variant="light" onClick={handleSignUp}>회원가입</Button>
                                        </>
                                    )}
                                </MypageConatiner>
                            )}
                        </div>
                    </Nav_Str>
                </Navbar.Brand>
            </Navbar>
        )
    }


