import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import winter from '../imgs/winter.jpg';
import axios from 'axios';
import { XMLParser } from 'fast-xml-parser';
import noImage from "../imgs/noImage.png"

const NewPage = (props) => {

    const [activeTab, setActiveTab] = useState(1);
    const [touristSpots, setTouristSpots] = useState([]);
    const [keyword, setKeyword] = useState("");
    const [searchKeyword, setSearchKeyword] = useState("");
    const [pageNo, setPageNo] = useState(1);

    const serviceKey = 'YRUALCBQQWvU6w/tG7ZkUtWtjAeaO9bJjyummGjvfF9SjR0QYO+CRveierZlwe97v5toXybLb6aoFCl1sZ8q4Q==';

    useEffect(() => {
        fetchData(keyword, pageNo);
    }, [pageNo]);

    const fetchData = async (searchKeyword, pageNo, retryCount = 0) => {
        try {
            const encodedServiceKey = encodeURIComponent(serviceKey);
            const encodedKeyword = encodeURIComponent(searchKeyword || "서울");
            const url = `http://apis.data.go.kr/B551011/KorService1/searchKeyword1?serviceKey=${encodedServiceKey}&MobileApp=AppTest&MobileOS=ETC&pageNo=${pageNo}&numOfRows=12&listYN=Y&keyword=${encodedKeyword}`;
            const response = await axios.get(url);

            const parser = new XMLParser();
            const jsonObj = parser.parse(response.data);

            if (jsonObj && jsonObj.response && jsonObj.response.body && jsonObj.response.body.items) {
                const spots = jsonObj.response.body.items.item;
                setTouristSpots(spots);
                setSearchKeyword(searchKeyword)
            } else {
                // 유효하지 않은 응답일 경우 재시도
                if (retryCount < 3) { // 최대 3번 재시도
                    fetchData(searchKeyword, pageNo, retryCount + 1);
                } else {
                    console.error("Invalid API response");
                }
            }
        } catch (error) {
            console.error('Error fetching data: ', error);
            if (retryCount < 3) {
                fetchData(searchKeyword, pageNo, retryCount + 1);
            }
        }
    };

    const fetchLocalData = async (searchKeyword, pageNo, retryCount = 0) => {
        try {
            const encodedServiceKey = encodeURIComponent(serviceKey);
            const encodedKeyword = encodeURIComponent(searchKeyword || "서울");
            const url = `http://apis.data.go.kr/B551011/KorService1/areaBasedList1?serviceKey=${encodedServiceKey}&pageNo=1&numOfRows=10&MobileApp=AppTest&MobileOS=ETC&arrange=A&areaCode=32`;
            const response = await axios.get(url);

            const parser = new XMLParser();
            const jsonObj = parser.parse(response.data);

            if (jsonObj && jsonObj.response && jsonObj.response.body && jsonObj.response.body.items) {
                const spots = jsonObj.response.body.items.item;
                setTouristSpots(spots);
                setSearchKeyword(searchKeyword)
            } else {
                // 유효하지 않은 응답일 경우 재시도
                if (retryCount < 3) { // 최대 3번 재시도
                    fetchData(searchKeyword, pageNo, retryCount + 1);
                } else {
                    console.error("Invalid API response");
                }
            }
        } catch (error) {
            console.error('Error fetching data: ', error);
            if (retryCount < 3) {
                fetchData(searchKeyword, pageNo, retryCount + 1);
            }
        }
    };

    const handleTabClick = (tabNumber) => {
        setActiveTab(tabNumber);
    };

    const renderTabContent = () => {
        switch (activeTab) {
            case 1:
                return (
                    <>
                        <TopContainer>
                            <TopImage className="NewCourse" alt="Title" src={winter} />
                        </TopContainer>
                        <MiddleContainer>
                            <MiddleBottomContainer>
                                <NewCourseContainer>
                                    <NewCourseTitle>신규 코스</NewCourseTitle>
                                </NewCourseContainer>
                            </MiddleBottomContainer>
                        </MiddleContainer>
                        <BottomContainer>
                            <MenuContainer>
                                <MenuTitle>
                                    <TabButton onClick={() => {handleTabClick(1);}} className={activeTab === 1 ? 'active' : ''}>신규 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(2);}} className={activeTab === 2 ? 'active' : ''}>테마별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(3);}} className={activeTab === 3 ? 'active' : ''}>지역별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(4);}} className={activeTab === 4 ? 'active' : ''}>추천 코스</TabButton>
                                </MenuTitle>
                            </MenuContainer>
                            <SmallTitleContainer>
                                <SmallTitle>
                                    <SmallTitleContent>[차 한잔으로 완성되는 휴식, 김포다도박물관]</SmallTitleContent>
                                </SmallTitle>
                                <SmallTag>{/* 이부분에 InputSkill 추가하면됨 */}</SmallTag>
                            </SmallTitleContainer>
                            <ContentContainer>
                                <ContentTitleContainer>
                                    <ContentSmallTitleContainer>모집 일정</ContentSmallTitleContainer>
                                </ContentTitleContainer>
                                <ContentMainContainer>
                                    <ContentMainTextContainer>
                                        <ContentTitle>
                                            <h2>웹 디자이너</h2>
                                            <h3>(경력)</h3>
                                        </ContentTitle>
                                        <ContentMemory>
                                            <h2>이런 일을 해요</h2>
                                            <ul>
                                                <li>11</li>
                                                <li>22</li>
                                                <li>33</li>
                                            </ul>
                                        </ContentMemory>
                                    </ContentMainTextContainer>
                                </ContentMainContainer>
                            </ContentContainer>
                        </BottomContainer>
                    </>
                );
            case 2:
                return (
                    <>
                        <TopContainer>
                            <TopImage className="NewCourse" alt="Title" src={winter} />
                        </TopContainer>
                        <MiddleContainer>
                            <MiddleBottomContainer>
                                <NewCourseContainer>
                                    <NewCourseTitle>테마별 코스</NewCourseTitle>
                                </NewCourseContainer>
                            </MiddleBottomContainer>
                        </MiddleContainer>
                        <BottomContainer>
                            <MenuContainer>
                                <MenuTitle>
                                    <TabButton onClick={() => {handleTabClick(1);}} className={activeTab === 1 ? 'active' : ''}>신규 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(2);}} className={activeTab === 2 ? 'active' : ''}>테마별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(3);}} className={activeTab === 3 ? 'active' : ''}>지역별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(4);}} className={activeTab === 4 ? 'active' : ''}>추천 코스</TabButton>
                                </MenuTitle>
                            </MenuContainer>
                        </BottomContainer>
                        <AreaContentContainer>
                            <SearchBoxContainer>
                                <SearchContainer>
                                    <SearchInput
                                        type="text"
                                        value={keyword}
                                        onChange={(e) => setKeyword(e.target.value)}
                                        placeholder="지역 검색"
                                    />
                                    <SearchButton onClick={() => fetchData(keyword, 1)}>검색</SearchButton>
                                </SearchContainer>
                                <SearchResultText>
                                    {searchKeyword ? `'${searchKeyword}' 검색 결과입니다.` : '전체 목록'}
                                </SearchResultText>
                            </SearchBoxContainer>
                            <GridContainer>
                                {Array.isArray(touristSpots) && touristSpots.length > 0 ? (
                                    touristSpots.map((spot, index) => (
                                        <GridItem key={index}>
                                            <h3>{spot.title}</h3>
                                            {spot.firstimage && <StyledImage src={spot.firstimage} alt={spot.title} />}
                                            {!spot.firstimage && <StyledImage src={noImage} alt={spot.title} />}
                                        </GridItem>
                                    ))
                                ) : (
                                    <p>Loading...</p>
                                )}
                            </GridContainer>
                            <PaginationContainer>
                                <PageButton onClick={() => setPageNo(prev => Math.max(prev - 1, 1))}>이전</PageButton>
                                <PageButton onClick={() => setPageNo(prev => prev + 1)}>다음</PageButton>
                            </PaginationContainer>
                        </AreaContentContainer>
                    </>
                );
            case 3:
                return (
                    <>
                        <TopContainer>
                            <TopImage className="NewCourse" alt="Title" src={winter} />
                        </TopContainer>
                        <MiddleContainer>
                            <MiddleBottomContainer>
                                <NewCourseContainer>
                                    <NewCourseTitle>지역별 코스</NewCourseTitle>
                                </NewCourseContainer>
                            </MiddleBottomContainer>
                        </MiddleContainer>
                        <BottomContainer>
                            <MenuContainer>
                                <MenuTitle>
                                    <TabButton onClick={() => {handleTabClick(1);}} className={activeTab === 1 ? 'active' : ''}>신규 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(2);}} className={activeTab === 2 ? 'active' : ''}>테마별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(3);}} className={activeTab === 3 ? 'active' : ''}>지역별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(4);}} className={activeTab === 4 ? 'active' : ''}>추천 코스</TabButton>
                                </MenuTitle>
                            </MenuContainer>
                        </BottomContainer>
                        <AreaContentContainer>
                            <SearchBoxContainer>
                                <SearchContainer>
                                    <SearchInput
                                        type="text"
                                        value={keyword}
                                        onChange={(e) => setKeyword(e.target.value)}
                                        placeholder="지역 검색"
                                    />
                                    <SearchButton onClick={() => fetchLocalData(keyword, 1)}>검색</SearchButton>
                                </SearchContainer>
                                <SearchResultText>
                                    {searchKeyword ? `'${searchKeyword}' 검색 결과입니다.` : '전체 목록'}
                                </SearchResultText>
                            </SearchBoxContainer>
                            <GridContainer>
                                {Array.isArray(touristSpots) && touristSpots.length > 0 ? (
                                    touristSpots.map((spot, index) => (
                                        <GridItem key={index}>
                                            <h3>{spot.title}</h3>
                                            {spot.firstimage && <StyledImage src={spot.firstimage} alt={spot.title} />}
                                            {!spot.firstimage && <StyledImage src={noImage} alt={spot.title} />}
                                        </GridItem>
                                    ))
                                ) : (
                                    <p>Loading...</p>
                                )}
                            </GridContainer>
                            <PaginationContainer>
                                <PageButton onClick={() => setPageNo(prev => Math.max(prev - 1, 1))}>이전</PageButton>
                                <PageButton onClick={() => setPageNo(prev => prev + 1)}>다음</PageButton>
                            </PaginationContainer>
                        </AreaContentContainer>
                    </>
                );
            case 4:
                return (
                    <>
                        <TopContainer>
                            <TopImage className="NewCourse" alt="Title" src={winter} />
                        </TopContainer>
                        <MiddleContainer>
                            <MiddleBottomContainer>
                                <NewCourseContainer>
                                    <NewCourseTitle>추천 코스</NewCourseTitle>
                                </NewCourseContainer>
                            </MiddleBottomContainer>
                        </MiddleContainer>
                        <BottomContainer>
                            <MenuContainer>
                                <MenuTitle>
                                    <TabButton onClick={() => {handleTabClick(1);}} className={activeTab === 1 ? 'active' : ''}>신규 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(2);}} className={activeTab === 2 ? 'active' : ''}>테마별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(3);}} className={activeTab === 3 ? 'active' : ''}>지역별 코스</TabButton>
                                    <TabButton onClick={() => {handleTabClick(4);}} className={activeTab === 4 ? 'active' : ''}>추천 코스</TabButton>
                                </MenuTitle>
                            </MenuContainer>
                        </BottomContainer>
                    </>
                );
            default:
                return null;
        }
    };

    return <Container>{renderTabContent()}</Container>;
};

