import React from "react";
import styled from "styled-components";
import {useNavigate} from "react-router-dom";
import new1 from "../imgs/newcourse-1.png";

const AboutPage = () => {
    const navigate = useNavigate();

    const Detailbutton = () => {
        navigate("/");
    }
    return (
            <AboutContainer>
                <TopAboutContainer>
                    <img src={new1} style={{width: '200px'}}/>
                </TopAboutContainer>
                <BottomAboutContainer>
                    <BottomCenterContainer>
                        <h2>우리의 생각</h2>
                        <h3>한국에는 아직 놀라울 정도로 넘치는 지역으로 가득합니다.</h3>
                        <h3>더 많은 사람들이 한국 각지에 부담없이 나가서 지역 각지에서 즐기는 세상을 만들고 싶습니다!</h3>
                        <h3>"한소연"은 그런 생각에서 태어난 서비스 입니다.</h3>
                        <DeatailButton onClick={Detailbutton}>자세히 보기</DeatailButton>
                    </BottomCenterContainer>
                </BottomAboutContainer>
            </AboutContainer>
    )
}


const AboutContainer = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  margin-top: 10rem;
  height: 500px;
  background-color: yellow;
`
const TopAboutContainer = styled.div`
  display: flex;
  flex-direction: row;
  flex: 5.5;
  background-color: blue;
`
const BottomAboutContainer = styled.div`
  display: flex;
  flex: 4.5;
  background-color: red;
  flex-direction: column;
  justify-content: center;
`
const BottomCenterContainer = styled.div`
  display: flex;
  flex: 1;
  background-color: #FDF9EA;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  h2 {
    font-size: 28px;
    color: orange;
    font-weight: 700;
  }
  h3 {
    font-size: 20px;
    font-weight: 600;
  }
`
const DeatailButton = styled.button`
  background-color: #EF6C00;
  color: white;
  font-weight: 500;
  border: none;
  border-radius: 3px;
  width: 250px;
  height: 35px;
`
export default AboutPage;
