import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecommendPage = () => {
    const [touristSpots, setTouristSpots] = useState([]);

    const serviceKey = 'YRUALCBQQWvU6w/tG7ZkUtWtjAeaO9bJjyummGjvfF9SjR0QYO+CRveierZlwe97v5toXybLb6aoFCl1sZ8q4Q==';

    useEffect(() => {
        const fetchData = async () => {
            try {
                const keyword = encodeURIComponent("강원");
                const url = `http://apis.data.go.kr/B551011/KorService1/searchKeyword1?serviceKey=${serviceKey}&MobileApp=AppTest&MobileOS=ETC&pageNo=1&numOfRows=10&listYN=Y&contTypeId=12&keyword=${keyword}`;
                console.log("Requesting: ", url); // 요청 URL 확인을 위한 콘솔 로그
                const response = await axios.get(url);
                console.log(response.data);
                // XML 응답 파싱 로직 필요 (API가 XML을 반환하는 경우)
            } catch (error) {
                console.error('Error fetching data: ', error);
            }

        };

        fetchData();
    }, []);


    return (
        <div>
            {touristSpots.length > 0 ? (
                <ul>
                    {touristSpots.map((spot, index) => (
                        <li key={index}>
                            <h3>{spot.title}</h3> {/* 'title'은 관광지의 제목 필드를 가정 */}
                            <p>{spot.description}</p> {/* 'description'은 설명 필드를 가정 */}
                            {/* 기타 필요한 정보를 표시 */}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default RecommendPage;
