import React from "react";
import styled from "styled-components";
import aboutPolicy from "../imgs/aboutPolicy.jpg";

const AboutPolicyPage = () => {
    return (
        <Container>
            <CenterContainer>
                <TopContainer>
                    <TopImgContainer>
                        <img src={aboutPolicy} alt="About Image" />
                    </TopImgContainer>
                </TopContainer>
                <BottomContainer>
                    <BottomContentContainer>
                        <h2>ddddddd</h2>
                        <h2>ddddddd</h2>
                        <h2>ddddddd</h2>
                        <h2>ddddddd</h2>

                    </BottomContentContainer>
                </BottomContainer>
            </CenterContainer>
        </Container>
    )
}

const Container = styled.div`
  display: flex;
  position: absolute;
  background-color: yellow;
  width: 100%;
  height: 100vh;
`
const CenterContainer = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  height: 80vh;
  background-color: green;
  margin-top: 8rem;
`
const TopContainer = styled.div`
  display: flex;
  flex: 4;
  background-color: red;
  justify-content: center;
`
const TopImgContainer = styled.div`
  display: flex;
  width: 90%;
  background-color: #61dafb;
  justify-content: center;
  align-items: center;

  img {
    width: 70%; /* 이미지 크기를 부모 컨테이너에 맞게 설정 */
    height: auto; /* 가로 비율에 맞추어 세로 비율을 자동으로 조정 */
    max-height: 100%;
    max-width: 100%;
    align-self: center;
  }
`
const BottomContainer = styled.div`
    display: flex;
  flex: 6;
  background-color: blue;
  flex-direction: column;
  align-items: center;
`
const BottomContentContainer = styled.div`
  background-color: orange;
  width: 90%;

`
export default AboutPolicyPage;
