import React from "react";
import styled from "styled-components";
import aboutPolicy from "../imgs/aboutPolicy.jpg";

const AboutPolicyPage = () => {
    return (
        <Container>
            <CenterContainer>
                <TopContainer>
                    <TopImgContainer>
                        <img src={aboutPolicy} alt="About Image" />
                    </TopImgContainer>
                </TopContainer>
                <BottomContainer>
                    <BottomContentContainer>
                        <h2>매번<br/></h2>
                        <h2>마음과 조건이 듬뿍 담긴 서비스입니다.<br/><br/></h2>
                        <h4>한국에는 아직 놀라울 정도로 매력 넘치는 지역들로 가득합니다.</h4>
                        <h4>더 많은 사람들이 한국 각지에 부담없이 나가서 지역에 들어가,</h4>
                        <h4>한국의 사랑스럽고 풍부한 매력에 빠지게 만들고 싶습니다.<br/></h4>
                        <h4>"한소연"은 그런 생각에서 태어난 서비스입니다.<br/></h4>
                        <h4>아무것도 없어 보이고, 모르는 사람들을 만나서 재밌습니다. </h4>
                        <h4>매번 가서 준 분들이 도움을 통해 지역에 빠져들고,</h4>
                        <h4>뭔가 멋진 발견이나 경험을 많이하고, 눈치채면 자신에게 있어서와 특별한 지역이 되어 돌아오는,</h4>
                        <h4>그런 새로운 여행의 형태를 제안할 수 있으면 하는 마음입니다.</h4>

                    </BottomContentContainer>
                </BottomContainer>
            </CenterContainer>
        </Container>
    )
}

const Container = styled.div`
  display: flex;
  position: absolute;
  background-color: yellow;
  width: 100%;
  height: 100vh;
`
const CenterContainer = styled.div`
  display: flex;
  flex: 1;
  flex-direction: column;
  height: 80vh;
  background-color: green;
  margin-top: 8rem;
`
const TopContainer = styled.div`
  display: flex;
  flex: 4;
  background-color: red;
  justify-content: center;
`
const TopImgContainer = styled.div`
  display: flex;
  width: 90%;
  background-color: #61dafb;
  justify-content: center;
  align-items: center;

  img {
    width: 70%; /* 이미지 크기를 부모 컨테이너에 맞게 설정 */
    height: auto; /* 가로 비율에 맞추어 세로 비율을 자동으로 조정 */
    max-height: 100%;
    max-width: 100%;
    align-self: center;
  }
`
const BottomContainer = styled.div`
    display: flex;
  flex: 6;
  background-color: blue;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-bottom: 3rem;
`
const BottomContentContainer = styled.div`
  background-color: orange;
  width: 90%;
  height: auto;
`
export default AboutPolicyPage;
