import {create} from "zustand";

/**
 * @typedef {Object} UserStore
 * @property {any} user
 * @property {(user: any) => void} setUser
 * @property {() => void} removeUser
 */

const useStore = create((set) => ({
    user: null,
    setUser: (user) => {
        set((state) => ({...state, user}));
    },
    removeUser: () => {
        set((state) => ({...state, user: null}));
    },

    // 사용자 프로필 사진을 업데이트하는 함수 추가
    updateProfilePic: (newProfilePicUrl) => {
        set((state) => {
            if (state.user) {
                // 사용자 객체 내의 프로필 사진 URL을 업데이트
                return { ...state, user: { ...state.user, userProfile: newProfilePicUrl }};
            }
            return state;
        });
    }
}));

export default useStore;
